import React from 'react'
import ReactDOM from 'react-dom'
import './index.css'

class ChatBar extends React.Component {
    componentDidMount() {
        this.chatEvent()
    }
    chatEvent() {
        document.getElementById("chatBox").addEventListener("keypress", (event) => {
            if (event.keyCode === 13) {
                event.preventDefault()
                document.getElementById("chatSend").click()
            }
        })
    }
    render() {
        return (
            <form>
                <input type="text" id="chatBox" autoFocus="autofocus"></input>
                <button type="button" id="chatSend" onClick={
                    () => {
                        let chatBox = document.getElementById("chatBox")
                        if (chatBox.value != "") this.props.onClick(chatBox.value)
                        chatBox.value = ""

                    }
                }
                >Send</button>
                <hr class="box"></hr>
            </form>
        )
    }
}

class ChatMsg extends React.Component {
    render() {
        return (
            <div>
                <h1>
                    {this.props.usr}
                </h1>
                <p>
                    {this.props.msg}
                </p>
            </div>
        )
    }
}

class Chat extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            history: []
        }
    }

    componentDidMount() {
        window.scrollTo(0, document.body.scrollHeight)
    }

    handleClick(value) {
        let state = this.state
        state.history.push({ usr: "helpme", msg: value })
        this.setState(state)
    }

    render() {
        return (
            <div>
                <ChatBar onClick={(value) => {
                    this.handleClick(value)
                }
                } />
                <div className="msg">
                {
                    this.state.history.map((item, index) => (
                        <ChatMsg key={index} msg={item.msg} usr={item.usr} />
                    ))
                }
                </div>
            </div>
        )
    }

}

// ========================================

ReactDOM.render(
    <Chat />,
    document.getElementById("root")
)
