import React from 'react'
import ReactDOM from 'react-dom'
import './index.css'

class ChatBar extends React.Component {
    componentDidMount() {
        this.chatEvent()
    }
    chatEvent() {
        document.getElementById("chatBox").addEventListener("keypress", (event) => {
            if (event.keyCode === 13) {
                event.preventDefault()
                document.getElementById("chatSend").click()
            }
        })
    }
    render() {
        return (
            <form>
                <input type="text" id="chatBox" autoFocus="autofocus"></input>
                <button type="button" id="chatSend" onClick={
                    () => {
                        this.props.onClick(document.getElementById("chatBox").value)
                    }
                }
                >Send</button>
            </form>
        )
    }
}

class ChatMsg extends React.Component {
    render() {
        return (
            <div>
                <h1>
                    {this.props.usr}
                </h1>
                <p>
                    {this.props.msg}
                </p>
            </div>
        )
    }
}

class Chat extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            history: [
                { usr: "eat", msg: "my ass" },
                { usr: "mother", msg: "pp" }
            ]
        }
    }

    handleClick(value) {
        this.state.history.push({usr: "helpme", msg: value})
    }

    render() {
        return (
            <div>
                <ChatMsg />
                <ChatBar onClick={(value) => {
                    this.handleClick(value)
                }
                } />
                {
                    this.state.history.map((item) => (
                        <ChatMsg key={item} class="msg" msg={this.state.history[item].msg} usr={this.state.history[item].usr} />
                    ))
                }
            </div>
        )
    }

}

// ========================================

ReactDOM.render(
    <Chat />,
    document.getElementById("root")
)
