import React from 'react'
import ReactDOM from 'react-dom'
import './index.css'

class ChatBar extends React.Component {
    componentDidMount() {
        this.chatEvent()
    }
    chatEvent() {
        document.getElementById("chatBox").addEventListener("keypress", (event) => {
            if (event.keyCode === 13) {
                event.preventDefault()
                document.getElementById("chatSend").click()
            }
        })
    }
    render() {
        return (
            <form>
                <input type="text" id="chatBox" autoFocus="autofocus"></input>
                <button type="button" id="chatSend" onClick={
                    () => {
                        this.props.onClick(document.getElementById("chatBox").value)
                    }
                }
                >Send</button>
            </form>
        )
    }
}

class ChatMsg extends React.Component {
    render() {
        return (
            <div>
                <h1>
                    {this.props.usr}
                </h1>
                <p>
                    {this.props.msg}
                </p>
            </div>
        )
    }
}

class Chat extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            msg1: { usr: "eat", msg: "my ass" }
        }
    }

    handleClick(value) {
        //this.setState({ history: value })
    }

    render() {
        return (
            <div>
                <ChatMsg />
                <ChatBar onClick={(value) => {
                    this.handleClick(value)
                }
                } />
                {
                    Object.keys(this.state).map(function (key, index) {
                        let msgKey
                        <ChatMsg class="msg" msg={msgKey.msg} usr={msgKey.usr} />
                    })
                }
            </div>
        )
    }

}

// ========================================

ReactDOM.render(
    <Chat />,
    document.getElementById("root")
)
