import React from 'react'
import ReactDOM from 'react-dom'
import './index.css'

class ChatBar extends React.Component {
    chatEvent() {
        // let chatBox = document.getElementById("chatBox")
        // chatBox.addEventListener("keypress", (event) => {
        //     if (event.keyCode === 13) {
        //         event.preventDefault()
        //         document.getElementById("chatSend").click()
        //     }
        // })
    }

    render() {
        return (
            <div>
                <input type="text" id="chatBox" onLoad={this.chatEvent()} autoFocus="autofocus"></input>
                <button type="button" id="chatSend" onClick={
                    () => {
                        let x = document.getElementById("chatBox").value
                        x = document.getElementById("chatBox").value
                        this.props.onClick(x)
                    }
                }
                >Send</button>
            </div>
        )
    }
}

class Chat extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            history: {}
        }
    }

    handleClick(value) {
        this.setState({ history: value })
        console.log(this.state)
    }

    render() {
        return (
            <div>
                <ChatBar onClick={(value) => this.handleClick(value)} />
            </div>
        )
    }

}

// ========================================

ReactDOM.render(
    <Chat />,
    document.getElementById("root")
)
