import React from 'react'
import ReactDOM from 'react-dom'
import './index.css'

class ChatBar extends React.Component {
    enterEvent() {
        let chatBox = document.getElementById("chatBox")
        if (chatBox) {
            chatBox.addEventListener("keypress", (event) => {
                if (event.keyCode === 13) {
                    event.preventDefault()
                    document.getElementById("chatSend").click()
                }
            })
            chatBox.addEventListener('input', typeHandler)
        }
    }

    render() {
        return (
            <div>
                <input type="text" id="chatBox"></input>
                <button type="button" id="chatSend" onClick={
                    () => {
                        this.props.onClick(document.getElementById("chatBox").value)
                    }
                }
                >Send</button>
                {this.enterEvent()}
            </div>
        )
    }
}

class Chat extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            history: {}
        }
    }

    handleClick(value) {
        this.setState({ history: value })
        console.log(this.state)
    }

    render() {
        return (
            <div>
                <ChatBar onClick={(value) => this.handleClick(value)} />
            </div>
        )
    }

}

// ========================================

ReactDOM.render(
    <Chat />,
    document.getElementById("root")
)
