import openSocket from 'socket.io-client'
const socket = openSocket('http://localhost:8000')

function subscribeToMsgs(cb) {
    socket.on('msgs', msgs => cb(null, msgs))
    socket.emit('subscribeToMsgs')
}

export { subscribeToMsgs }
