import React from 'react'
import ReactDOM from 'react-dom'
import Markup from 'interweave'
import './index.css'
const fs = require('fs')

function linkify(text) {
    // eslint-disable-next-line
    let urlRegex = /(\b(https?|ftp|file):\/\/[-A-Z0-9+&@#\/%?=~_|!:,.;]*[-A-Z0-9+&@#\/%=~_|])/ig
    return text.replace(urlRegex, function (url) {
        return '<a href="' + url + '">' + url + '</a>'
    })
}

class ChatBar extends React.Component {
    componentDidMount() {
        this.chatEvent()
    }
    chatEvent() {
        document.getElementById("chatBox").addEventListener("keypress", (event) => {
            if (event.keyCode === 13) {
                event.preventDefault()
                document.getElementById("chatSend").click()
            }
        })
        document.getElementById("usrBox").addEventListener("keypress", (event) => {
            if (event.keyCode === 13) {
                event.preventDefault()
            }
        })
    }
    render() {
        return (
            <form>
                <input type="text" id="usrBox" autoComplete="off"></input>
                <input type="text" id="chatBox" autoFocus="autofocus" autoComplete="off"></input>
                <button type="button" id="chatSend" onClick={
                    () => {
                        let chatBox = document.getElementById("chatBox")
                        let usrBox = document.getElementById("usrBox")
                        let usr
                        if (!usrBox.value) usr = "Anonymous"
                        else usr = usrBox.value
                        if (chatBox.value !== "") this.props.onClick(chatBox.value, usr)
                        chatBox.value = ""

                    }
                }
                >Send</button>
                <hr className="box"></hr>
            </form>
        )
    }
}

class ChatMsg extends React.Component {
    render() {
        return (
            <div>
                <h3>
                    {this.props.usr}
                </h3>
                <p>
                    <Markup content={linkify(this.props.msg)} />
                </p>
            </div>
        )
    }
}

class Chat extends React.Component {
    constructor(props) {
        super(props)
        this.state = this.getText()
    }

    getText() {
        fs.readFile('history.json', 'utf8', function (err, data) {
            if (err) throw err
            return (JSON.parse(data))
        })
    }

    handleClick(value, usrName) {
        let state = this.state
        state.history.push({ usr: usrName, msg: value })
        this.setState(state, () => {
            let scrollingElement = (document.scrollingElement || document.body)
            scrollingElement.scrollTop = scrollingElement.scrollHeight
        }
        )
    }

    render() {
        return (
            <div>
                <ChatBar onClick={(value, usrName) => {
                    this.handleClick(value, usrName)
                }
                } />
                <div className="msg">
                    {
                        this.state.history.map((item, index) => (
                            <ChatMsg key={index} msg={item.msg} usr={item.usr} />
                        ))
                    }
                </div>
            </div>
        )
    }

}

// ========================================

ReactDOM.render(
    <Chat />,
    document.getElementById("root")
)
