import React from 'react'
import ReactDOM from 'react-dom'
import './index.css'

class ChatBar extends React.Component {
    render() {
        return (
            <form>
                <input type="text" id="chatBox"></input>
                <button type="button" id="chatSend" onClick={
                    () => { this.props.onClick(document.getElementById("chatBox").value) }
                }
                >Send</button>
                {
                    document.getElementById("chatBox").addEventListener("keypress", function (event) {
                        if (event.keyCode === 13) {
                            event.preventDefault()
                            document.getElementById("chatSend").click()
                        }
                    })
                }
            </form>
        )
    }
}

class Chat extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            history: {}
        }
    }

    handleClick(value) {
        this.state.history.text = value
        console.log(this.state)
    }

    render() {
        return (
            <div>
                <ChatBar onClick={(value) => this.handleClick(value)} />
            </div>
        )
    }

}

// ========================================

ReactDOM.render(
    <Chat />,
    document.getElementById("root")
)
